$.cleared=function(a){if($(a).length){$(a).each(function(){var b=$(this).val();$(this).focus(function(){if($(this).val()==b){$(this).val("")}});$(this).blur(function(){if($(this).val()==""){$(this).val(b)}})})}};$.validate=function(a){if($(a).length){$(a).submit(function(){var f=$(this).attr("action");var g=$(this).find("input#fname").attr("name");var c=$(this).find("input#lname").attr("name");var d=$(this).find("input#email").attr("name");if(f==""||g==""||c==""||d==""){alert("The newsletter form is not configured properly. Update the newsletter settings in the theme editor in your shop admin.");return false}var e=$(this).find("input#lname").val();if(e=="Last Name..."){alert("The last name field cannot be blank.");$(a).find("input#lname").focus();return false}var b=$(this).find("input#email").val();if(!$.regex(b)){alert("Please enter a valid email address to subscribe.");$(a).find("input#email").focus();return false}});$.regex=function(b){var c=new RegExp(/^(("[\w-\s]+")|([\w-]+(?:\.[\w-]+)*)|("[\w-\s]+")([\w-]+(?:\.[\w-]+)*))(@((?:[\w-]+\.)*\w[\w-]{0,66})\.([a-z]{2,6}(?:\.[a-z]{2})?)$)|(@\[?((25[0-5]\.|2[0-4][0-9]\.|1[0-9]{2}\.|[0-9]{1,2}\.))((25[0-5]|2[0-4][0-9]|1[0-9]{2}|[0-9]{1,2})\.){2}(25[0-5]|2[0-4][0-9]|1[0-9]{2}|[0-9]{1,2})\]?$)/i);return c.test(b)}}};$(document).ready(function(){$.cleared("input.field");$.validate("li.newsletter form")});